// rutas
app.config(function($routeProvider) {
  $routeProvider
    .when('/', {
      templateUrl : 'templates/task.html',
      controller  : 'taskController'
    })

    .when('/task/:taskId', {
      templateUrl : 'templates/detailTask.html',
      controller  : 'commentsController'
    })

    .when('/todo/', {
      templateUrl : 'templates/todo.html',
      controller  : 'todoController'
    })

    .when('/client/', {
      templateUrl : 'templates/client.html',
      controller  : 'clientController'
    })

    .when('/2/', {
      templateUrl : 'templates/task2.html',
      controller  : 'taskController'
    })

    .when('/3/', {
      templateUrl : 'templates/task3.html',
      controller  : 'taskController'
    })

    .otherwise({
      redirectTo: '/'
    });
});

// directiva para calendario
app.directive('jqdatepicker', function () {
    return {
        restrict: 'A',
        require: 'ngModel',
         link: function (scope, element, attrs, ngModelCtrl) {
            element.datepicker({
                dateFormat: 'yy-mm-dd',
                onSelect: function (date) {
                    scope.date = date;
                    scope.$apply();
                }
            });
        }
    };
});

app.directive('fileModel', ['$parse', function ($parse) {
    return {
        restrict: 'A',
        link: function(scope, element, attrs) {
            var model = $parse(attrs.fileModel);
            var modelSetter = model.assign;

            element.bind('change', function(){
                scope.$apply(function(){
                    modelSetter(scope, element[0].files[0]);
                });
            });
        }
    };
}]);

app.service('fileUpload', ['$http', function ($http) {
    this.uploadFileToUrl = function(taskId, userId, comment, file, uploadUrl){
        var fd = new FormData();
        fd.append('task', taskId);
        fd.append('user', userId);
        fd.append('comment', comment);
        fd.append('docfile', file);
        $http.post(uploadUrl, fd, {
            transformRequest: angular.identity,
            headers: {'Content-Type': undefined}
        })
        .success(function(){
        })
        .error(function(){
        });
    }
}]);

