// convierte en html seguro
app.filter('rawHtml', ['$sce', function($sce){
  return function(val) {
    return $sce.trustAsHtml(val);
  };
}]);

app.filter('lastWord', ['$sce', function($sce){
  return function(txt) {
	if(txt){
		var txtCut = txt.split("/"); 
	    return txtCut[txtCut.length-1];
	}else{
		return false;	
	}
  };
}]);

