app.controller('taskController', function ($scope, taskResource, clientResource, $uibModal) {

  $scope.vm = {
    done : '',
    client : ''
  };

  // obtiene las tareas
  function getTasks() {
    $scope.tasks = taskResource.query({ done: 'False'});
  }
  getTasks();

  // marca como realizada la tarea
  $scope.doneTask = function(taskId) {
    taskResource.get({ taskId:taskId }, function(task) {
      task.done = true;
      taskResource.update({ taskId:task.id }, task);
    });
    getTasks();
  };

  // borra la tarea
  $scope.deleteTask = function(taskId) {
    if (confirm("Esta seguro de borrar la tarea ?")) {
      taskResource.delete({ taskId });
      getTasks();
    }
  };

  // obtiene todos los clientes
  getClients = function() {
    clientResource.query({}, function(data){
      all = {
        id : '',
        name : 'Todos',
        lastname : ''
      }
      data.unshift(all);
      $scope.clients = data;
    });
  };
  getClients();

  $scope.open = function (size) {        
    var modalInstance = $uibModal.open({
        size: size,
        animation: false,
        backdrop: 'static',
        templateUrl: 'taskmodal.html',
        controller: 'customerModalCtrl',            
        resolve: {
            customer: function () {
                return $scope.customer;
            }
        }
    });
  }
})

